var dbconnection = dbconnection || {};
var mysql = require('mysql');
var appconfig = require('./appconfig.js').Configurations;
(function (connection) {
	connection.CreateConnection = function (callback) {
		db=mysql.createConnection(appconfig.Database);
		db.connect(function (err) {
			if (err) {
				callback({status:'error',response:err});
			}
			callback({ status: 'success', response: db });
		});
	};	
}(dbconnection));

module.exports = dbconnection;